mapper.select:
select count(*)
	from member
	where mem_id = #{memId}

mapper.update:
update member
 set mem_pwd = #{memPwd}
 where mem_id = #{memId}

mapper.select:
select count(*)
from board
where board_active='Y' and board_writer_no = #{memNo}

select cate_no, board_title, board_writer_name, create_date
from board
where board_active='Y' and board_writer_no = #{memNo}

select count(*)
from reply
where mem_no = #{memNo}

select mem_Id
from member
where mem_Name = #{memName} and mem_phone = #{memPhone}

select *
from member
where mem_id = #{memId} and mem_name = #{memName} and mem_phone = #{memPhone}
