mapper.select:
select count(*)
	from member
	where mem_id = #{memId}

mapper.update:
update member
 set mem_pwd = #{newPwd}, mem_phone = #{memPhone}
 where mem_id = #{id}

mapper.select:
select count(*)
from IN_BO_BOARD_VIEW
where board_active='Y' and writer_no = #{memNo}

mapper.update:
update member
set mem_active = 'N'
where mem_no = ${memNo}

mapper.select:
select mem_Id
from member
where mem_Name = #{memName} and mem_phone = #{memPhone}

select *
from member
where mem_id = #{memId} and mem_name = #{memName} and mem_phone = #{memPhone}

update board
set board_active='N'
where board_no = ${boNo}

select count(*)
from in_bo_board_view

	where board_active='Y' and writer_no = ${memNo}


	where board_active='Y' and CATE_NO = ${noSubject} and writer_no = ${memNo}

select *
from in_bo_board_view
where board_active='Y' 

	and writer_no = ${memNo} 


	and CATE_NO = ${noSubject} and writer_no = ${memNo}

order by board_no desc

mapper.update:
update reply
set re_active = 'N'
where re_no = ${boNo}

update board
	set board_title = #{title}, board_content = #{content}, modify_date = sysdate
	where board_no = #{boardNo}

update inquiry_book
	set in_bo_phone = #{writerPhone}, in_bo_pwd = #{boPwd}
	where board_no = #{boardNo}

update member
set MEM_INTRO = #{greetingWrite}
where MEM_NO = #{memNo}
